import configparser
import synapseclient
import os
import timeit
from synapse_span_table import SynapseSpanTable

MAX_STRING_LEN = 50
TABLE_PREFIX = 'arc_mnh_'
tableName = TABLE_PREFIX + 'test'
columnLimit = 3

# Config.
config = configparser.ConfigParser()
config.read(os.path.join(os.getcwd(), 'config.ini'))
config.sections()

# Connect to Synapse.
syn = synapseclient.Synapse()
synProjectName = config['SYNAPSE']['ProjectName']
synUserName = config['SYNAPSE']['UserName']
apiKey = config['SYNAPSE']['apiKey']
syn.login(email=synUserName, apiKey=apiKey)


def afterTest():
    global syn
    global synProjectName
    children = syn.getChildren(synProjectName)
    for entity in children:
        syn.delete(entity['id'])


#
# Queue records with different schema in the same table then flush
#
TestName = 'Queue records with different schema in the same table then flush'
data1 = {
    "id": "1",
    "a": "1",
    "b": "1",
    "c": "1",
    "d": "1",
    "e": "1",
    "f": "data:image/jpeg;base64,",
    "g": "1"
}
data2 = {
    "id": "2",
    "a": "2",
    "b": "2",
    "c": "2",
    "p": "2",
    "d": "2",
    "f": "data:image/jpeg;base64,",
    "g": "2",
    "h": "2",
    "i": "2",
    "j": "2"
}
start_time = timeit.default_timer()

synapse_span_table = SynapseSpanTable(syn, synProjectName, columnLimit=columnLimit)
synapse_span_table.queue_span_table_record(tableName, data1)
synapse_span_table.queue_span_table_record(tableName, data2)
synapse_span_table.flush_span_tables()

record1 = synapse_span_table.read_span_table_record(tableName, data1['id'])
record2 = synapse_span_table.read_span_table_record(tableName, data2['id'])
allValuesMatch = True
for key in data1.keys():
    if record1[key][:MAX_STRING_LEN] != data1[key][:MAX_STRING_LEN]:
        allValuesMatch = False
for key in data2.keys():
    if record2[key][:MAX_STRING_LEN] != data2[key][:MAX_STRING_LEN]:
        allValuesMatch = False
if allValuesMatch:
    end_time = timeit.default_timer()
    print('Passed: ' + TestName)
    print('Test Time: ' + str(int(end_time - start_time)) + ' seconds')
else:
    raise RuntimeError('Failed: ' + TestName)
afterTest()

#
# Queue the same record with different schema in the same table then flush
#

TestName = 'Queue the same record with different schema in the same table then flush'
data = {
    "id": "1",
    "a": "1",
    "b": "1",
    "c": "1",
    "d": "1",
    "e": "1",
    "f": "data:image/jpeg;base64,",
    "g": "1"
}
start_time = timeit.default_timer()

synapse_span_table = SynapseSpanTable(syn, synProjectName, columnLimit=columnLimit)
synapse_span_table.queue_span_table_record(tableName, data)
synapse_span_table.flush_span_tables()

data['g'] = '1a'
synapse_span_table.flexsert_span_table_record(tableName, data)
recordUpdated = synapse_span_table.read_span_table_record(tableName, data['id'])
if recordUpdated['g'] == '1a':
    end_time = timeit.default_timer()
    print('Passed: ' + TestName)
    print('Test Time: ' + str(int(end_time - start_time)) + ' seconds')
else:
    raise RuntimeError('Failed: ' + TestName)
afterTest()

